insert into t_functions ("id", "name", "args", "description", "doclink", "driver") values ('4033', 'OVERLAY', '', 'OVERLAY() overwrites part of a string with another string. By default, the number of characters removed from (overwritten in) the host string equals the length of the replacement string. With the optional fourth argument, a different number of characters can be specified for removal.', 'https://www.firebirdsql.org/file/documentation/chunk/en/refdocs/fblangref25/fblangref25-functions-string.html#fblangref25-functions-scalarfuncs-overlay', 'QIBASE');
