insert into t_functions ("id", "name", "args", "description", "doclink", "driver") values ('4066', 'DECODE', '', 'DECODE is a shorthand for the so-called "simple CASE" construct, in which a given expression is compared to a number of other expressions until a match is found. The result is determined by the value listed after the matching expression. If no match is found, the default result is returned, if present. Otherwise, NULL is returned.', 'https://www.firebirdsql.org/file/documentation/chunk/en/refdocs/fblangref25/fblangref25-functions-conditional.html#fblangref25-functions-scalarfuncs-decode', 'QIBASE');
