insert into t_functions ("id", "name", "args", "description", "doclink", "driver") values ('4068', 'MAXVALUE', '', 'Returns the maximum value from a list of numerical, string, or date/time expressions. This function fully supports text BLOBs of any length and character set.', 'https://www.firebirdsql.org/file/documentation/chunk/en/refdocs/fblangref25/fblangref25-functions-conditional.html#fblangref25-functions-scalarfuncs-maxvalue', 'QIBASE');
insert into t_functions ("id", "name", "args", "description", "doclink", "driver") values ('4069', 'MINVALUE', '', 'Returns the minimum value from a list of numerical, string, or date/time expressions. This function fully supports text BLOBs of any length and character set.', '', 'QIBASE');
insert into t_functions ("id", "name", "args", "description", "doclink", "driver") values ('4070', 'NULLIF', '', 'NULLIF returns the value of the first argument, unless it is equal to the second. In that case, NULL is returned.', 'https://www.firebirdsql.org/file/documentation/chunk/en/refdocs/fblangref25/fblangref25-functions-conditional.html#fblangref25-functions-scalarfuncs-nullif', 'QIBASE');
insert into t_functions ("id", "name", "args", "description", "doclink", "driver") values ('4071', 'AVG', '', 'AVG returns the average argument value in the group. NULL is ignored.', 'https://www.firebirdsql.org/file/documentation/chunk/en/refdocs/fblangref25/fblangref25-functions-aggfuncs.html#fblangref25-functions-aggfuncs-avg', 'QIBASE');
insert into t_functions ("id", "name", "args", "description", "doclink", "driver") values ('4072', 'COUNT', '', 'COUNT returns the number of non-NULL values in a group.', 'https://www.firebirdsql.org/file/documentation/chunk/en/refdocs/fblangref25/fblangref25-functions-aggfuncs.html#fblangref25-functions-aggfuncs-count', 'QIBASE');
insert into t_functions ("id", "name", "args", "description", "doclink", "driver") values ('4073', 'LIST', '', 'LIST returns a string consisting of the non-NULL argument values in the group, separated either by a comma or by a user-supplied separator. If there are no non-NULL values (this includes the case where the group is empty), NULL is returned.', 'https://www.firebirdsql.org/file/documentation/chunk/en/refdocs/fblangref25/fblangref25-functions-aggfuncs.html#fblangref25-functions-aggfuncs-list', 'QIBASE');
insert into t_functions ("id", "name", "args", "description", "doclink", "driver") values ('4074', 'MAX', '', 'MAX returns the maximum non-NULL element in the result set.', 'https://www.firebirdsql.org/file/documentation/chunk/en/refdocs/fblangref25/fblangref25-functions-aggfuncs.html#fblangref25-functions-aggfuncs-max', 'QIBASE');
